sparql:
SELECT DISTINCT ?item ?itemLabel WHERE {
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE]". }
  {
    SELECT DISTINCT ?item ?date_update WHERE {
      ?item_biography p:P361 ?statement0.
      ?statement0 (ps:P361/(wdt:P279*)) wd:Q130268200.
      ?item_biography p:P921 ?statement1.
      ?statement1 (ps:P921/(wdt:P279*)) ?item.
    }
    LIMIT 10000
  }
} ORDER BY ?itemLabel
